// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Loyalty {
    mapping(address => uint256) public points;
    address public owner;

    event PointsAwarded(address indexed user, uint256 amount);
    event PointsRedeemed(address indexed user, uint256 amount, string barcode);

    constructor() {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    function awardPoints(address user, uint256 amount) public onlyOwner {
        points[user] += amount;
        emit PointsAwarded(user, amount);
    }

    function checkPoints(address user) public view returns (uint256) {
        return points[user];
    }

    function redeemPoints() public returns (string memory) {
        uint256 amount = points[msg.sender];
        require(amount > 0, "No points to redeem");

        points[msg.sender] = 0;

        string memory barcode = generateBarcode(msg.sender, amount);
        emit PointsRedeemed(msg.sender, amount, barcode);
        return barcode;
    }

    function generateBarcode(address user, uint256 amount) internal view returns (string memory) {
        return string(abi.encodePacked("BAR-", toAsciiString(user), "-", uint2str(amount)));
    }

    function toAsciiString(address x) internal pure returns (string memory) {
        bytes memory s = new bytes(40);
        for (uint256 i = 0; i < 20; i++) {
            bytes1 b = bytes1(uint8(uint(uint160(x)) / (2**(8*(19 - i)))));
            bytes1 hi = bytes1(uint8(b) / 16);
            bytes1 lo = bytes1(uint8(b) - 16 * uint8(hi));
            s[2*i] = char(hi);
            s[2*i+1] = char(lo);
        }
        return string(s);
    }

    function char(bytes1 b) internal pure returns (bytes1 c) {
        if (uint8(b) < 10) return bytes1(uint8(b) + 48);
        else return bytes1(uint8(b) + 87);
    }

    function uint2str(uint256 _i) internal pure returns (string memory) {
        if (_i == 0) return "0";
        uint256 j = _i;
        uint256 len;
        while (j != 0) {
            len++;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint256 k = len;
        while (_i != 0) {
            k--;
            bstr[k] = bytes1(uint8(48 + _i % 10));
            _i /= 10;
        }
        return string(bstr);
    }
}

